(function ($) {
    $.extend({
       btn_hover:function () {
           $(".md-btn").hover(function () {
               $(this).animate({
                   "border-radius":"20px"
               });
           },function () {
               $(this).animate({
                   "border-radius":"4px"
               });
           })
       },
        /*弹出层居中显示*/
        popups:function () {
            var poTop = $(".popups-top").height();
            var poFooter = $(".popups-footer").height();
            var w = $(".popups-content").width();
            var h = $(".popups-content").height();
            $(".popups-con-text").css("height",h-poTop-poFooter);
            $(".popups-content").css({top:($(window).height()-h)/2,left:($(window).width()-w)/2})
            $(".md-btn").on("click",function () {
                $(".popups").each(function (i) {
                    // alert(i)
                    $(this).eq(i).show()
                })
            })
        },
        popopusClose:function () {
            $(".popups-content").on("click",function (e) {
                e.stopPropagation()
            })
            $(".popups").on("click",function () {
                $(this).hide()
            })
            $(".icon-close").on("click",function () {
                $(this).parents(".popups").hide()
            })
            $(".color-cancel").on("click",function () {
                $(this).parents(".popups").hide()
            })
        }

    })
})(jQuery)